(function($) {
$(document).ready(function(){
	var ajaxurl = '/wp-admin/admin-ajax.php';
    
    // Smart Menu
	
	$(".menu-toggle").click(function() {
		$('.overmenu').toggleClass('active');
	});
    
    $(".mclose").click(function() {
		$('.overmenu').toggleClass('active');
	});
    
    
    // Mobile menu with double click
    
    if ($(window).width() < 991) {
        var used;
        $(".overmenu li.menu-item-has-children > a").on("click", function(e){
            if( used !== this) {
                used = this;
                e.preventDefault();

                $(this).parent().children('ul').slideToggle();
                $(this).parent().siblings().children('ul').slideUp('fast');
            }
        });
    }
    
    
    // Search
    
    $('.hsearch, .soc-search').click(function() {
		$('.oversearch').toggleClass('active');
        $(".oversearch input[type='text']").focus();
		$('#datafetch').html('');
	});
    
    $(".sclose").click(function() {
		$('.oversearch').toggleClass('active');
        $('.results-container .fp-bot').remove();
	});
    
    $('.oversearch .sinput').keypress(function(eventObject){
		var searchTerm = $(this).val();
		// проверим, если в поле ввода более 2 символов, запускаем ajax
		if(searchTerm.length > 2){
			$.ajax({
				url: ajaxurl,
				type: "POST",
				data: {
					action: 'data_fetch',
					keyword: searchTerm,
				},
				success: function(data) {
                    if(data) {
                        $('#datafetch').html(data);
                        $('.results-container .fp-bot').remove();
                        $('.results-container').append($('<div class="fp-bot"><a href="https://sultan-tv.ru/?s='+$(".oversearch .sinput").val()+'" class="btn">Смотреть все результаты</a></div>'));
                    } else {
                        $('#datafetch').html('');
                        $('.results-container .fp-bot').remove();
                    }
				}
			});
		}
	});
    
    
    // Ajax pagination actors news
	
	$('.postainer').on('click', '.titler-arrow', function(){
        var tainer = $(this).data('tainer');
		var post_name = $(this).data('postype');
		var cat_name = $(this).data('catname');
        var tag_name = $(this).data('tagname');
        var term_name = $(this).data('termname');
        var char_name = $(this).data('charname');
		var mypost_id = $(this).data('postid');
		var mypage = $(this).data('page');
        var per_page = $(this).data('perpage');

        $.ajax({
            type: "POST",
            url: ajaxurl,
            data: {
                action: 'uni_get_terms',
                tainer: tainer,
				postype: post_name,
				category: cat_name,
                tag: tag_name,
                term: term_name,
                char: char_name,
				mypostid: mypost_id,
				paged: mypage,
                perpage: per_page,
            },
            success: function (data) {
                $('#ajax-container-' + tainer).html(data);
            }
        });
    });
	
    
    // Scroll to top
        
    var totopbtn = $('#scroll-to-top');
    $(window).scroll(function() {
        if ($(window).scrollTop() > 300) {
            totopbtn.addClass('active');
        } else {
            totopbtn.removeClass('active');
        }
    });

    totopbtn.on('click', function(e) {
        //e.preventDefault();
        $('html, body').animate({scrollTop:0}, '300');
    });
    
    
    // Cookies
    // Social Media Modal
    
    //Cookies.set("socpop", "1", { expires:0, path: '/' });
    //Cookies.set("notifypop", "1", { expires:0, path: '/' });

    $("#socup-close").click(function () {
        Cookies.set("socpop", "1", { expires:30, path: '/' });
    });

    if ($(window).width() < 992) {
        if ( Cookies.get("socpop") == null ) {
            setTimeout(function(){
                $("#socup").modal();
            }, 20000)
        }
    }
    //console.log(document.cookie);
    
    $(document).on('click', '.cookie_accept', function () {
        Cookies.set("notifypop", "1", { expires:365, path: '/' });
        $("#cookie_notification").hide();
    });

    if ( Cookies.get("notifypop") == null ) {
        $("#footer").append('<div id="cookie_notification"><p>Для улучшения работы сайта и его взаимодействия с пользователями мы используем файлы cookie. Продолжая работу с сайтом, Вы разрешаете использование cookie-файлов. Вы всегда можете отключить файлы cookie в настройках Вашего браузера.</p><button class="button cookie_accept">Принять</button></div>');
    }
    
});
	
})(jQuery);